import * as axios from "axios";

const instance = axios.create({
    baseURL: 'data.json',
    withCredentials: true,
    headers: {}
});

export const state = instance.get().then(response => {return response});

const getAPI = {
    getConfig() {
        return instance.get('config').then(response => {return response});
    },
    getData() {
        return instance.get('data').then(response => {return response.data});
    },
    getGameState() {
        return instance.get('gameState').then(response => {return response.data});
    }
}

export default getAPI;
