import getAPI from "../api/api"

var initialState = [];

let state = getAPI.getConfig().then(response => {return response.data});

console.log(state);

const SET_QUESTIONS_AMOUNT = 'SET_QUESTIONS_AMOUNT';
const SET_ANSWERS_AMOUNT = 'SET_ANSWERS_AMOUNT';
const SET_CORRECT_ANSWERS_AMOUNT = 'SET_CORRECT_ANSWERS_AMOUNT';

const configReducer = (state = initialState, action) => {

    switch (action.type) {
        case SET_QUESTIONS_AMOUNT: {
            return { ...state, questionsAmount: action.questionsAmount };
        }
        case SET_ANSWERS_AMOUNT: {
            return { ...state, answersAmount: action.answersAmount }
        }
        case SET_CORRECT_ANSWERS_AMOUNT: {
            return { ...state, correctAnswersAmount: action.correctAnswersAmount }
        }
        default:
            return state;
    }
}

export const setQuestionsAmount = (questionsAmount) => ({ type: SET_QUESTIONS_AMOUNT, questionsAmount })
export const setAnswersAmount = (answersAmount) => ({ type: SET_ANSWERS_AMOUNT, answersAmount })
export const setCorrectAnswersAmount = (correctAnswersAmount) => ({ type: SET_CORRECT_ANSWERS_AMOUNT, correctAnswersAmount });

window.state = state;

export default configReducer;
