import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { removeQuestion } from '../redux/data-reducer';
import { setCurrentQuestion, setLevel, setScore } from '../redux/gameState-reducer';
import Preloader from './../assets/Preloader.svg'
import Answers from './answers';
import Question from './Question';
import ScoreBoard from './score';

const Game = (props) => {
    useEffect(() => {
        if(props.questions !== undefined) {
            let index = props.questions.indexOf(props.questions[Math.floor(Math.random() * props.questions.length)]);
            props.setCurrentQuestion(props.questions[index]);
            props.removeQuestion(index);
        }
    }, [props.initialized]);
    return (
        <div>
            {props.currentQuestion === null && <Preloader />}
            {props.currentQuestion !== null && props.currentQuestion !== undefined && <div>
                <Question question={props.currentQuestion.question}/>
                <Answers answers={props.currentQuestion.content}/>
                <ScoreBoard score={props.score} level={props.currentLevel}/>
            </div>
            }
            {/*  */}
        </div>
    )
}

const mapStateToProps = (state) => ({
    initialized: state.game.appInitialized,
    questions: state.data.questions,
    currentQuestion: state.game.currentQuestion,
    currentLevel: state.game.currentLevel,
    score: state.game.currentScore
})

export default compose(connect(mapStateToProps, { setCurrentQuestion, setScore, setLevel, removeQuestion })(Game));
