import React from 'react';
import s from './scoreItem.module.css';

const ScoreBoard = (props) => {
    let board;

    if (props.scorePoints) {
        board = props.scorePoints.map((e, i) => <ScoreItem key={i} amount={e} level={props.level} id={i} />);
    };

    return (
        <div className={s.board}>
            {board}
        </div>
    )
}

{ props.level > (props.id + 1) && <div className={s.passed}>{props.amount}</div> }
{ props.level < (props.id + 1) && <div className={s.possible}>{props.amount}</div> }

const ScoreItem = (props) => {
    return (
        <div>
            {props.level === (props.id + 1) && <svg width="240" height="40" viewBox="0 0 240 40" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M13.4526 4.63788C15.6376 2.01596 18.8742 0.5 22.2872 0.5H217.713C221.126 0.5 224.362 2.01597 226.547 4.63788L239.349 20L226.547 35.3621C224.362 37.984 221.126 39.5 217.713 39.5H22.2872C18.8742 39.5 15.6376 37.984 13.4526 35.3621L0.650853 20L13.4526 4.63788Z" fill="white" stroke="#D0D0D8" />
            </svg>}
        </div>
    )
}

export default ScoreBoard;
