import getAPI from "../api/api"

let initialState = [];

const SET_CURRENT_SCORE = 'SET_CURRENT_SCORE';
const SET_STATE = 'SET_STATE';
const SET_CURRENT_QUESTION = 'SET_ANSWERS_AMOUNT';
const SET_CURRENT_LEVEL = 'SET_CORRECT_ANSWERS_AMOUNT';

const gameStateRecuder = (state = initialState, action) => {

    switch (action.type) {
        case SET_STATE: {
            return { ...action.state};
        }
        case SET_CURRENT_SCORE: {
            return { ...state, currentScore: action.currentScore };
        }
        case SET_CURRENT_QUESTION: {
            return { ...state, currentQuestion: action.currentQuestion }
        }
        case SET_CURRENT_LEVEL: {
            return { ...state, currentLevel: action.currentLevel }
        }
        default:
            return state;
    }
}

export const getGameState = () => (dispatch) => {
    getAPI.getGameState()
    .then(response => {
        console.log(response)
        dispatch(setState(response));
    })
};

const setState = (state) => ({ type: SET_STATE, state });
export const setScore = (currentScore) => ({ type: SET_CURRENT_SCORE, currentScore })
export const setCurrentQuestion = (currentQuestion) => ({ type: SET_CURRENT_QUESTION, currentQuestion })
export const setLevel = (currentLevel) => ({ type: SET_CURRENT_LEVEL, currentLevel });

export default gameStateRecuder;
