import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { removeQuestion } from '../redux/data-reducer';
import { setCurrentQuestion, setLevel } from '../redux/gameState-reducer';
import Preloader from './../assets/Preloader.svg'


const Answers = (props) => {
    const [clickedId, getClickedId] = useState(null);
    const handleClick = (id) => {
        console.log(id);
        getClickedId(id);
    };
    useEffect(() => {
        if (props.correctAnswer === clickedId) {
            setLevel(props.level + 1);
            let index = props.questions.indexOf(props.questions[Math.floor(Math.random() * props.questions.length)]);
            props.setCurrentQuestion(props.questions[index]);
            props.removeQuestion(index);
            getClickedId(null);
        } else {
            
        }
    }, [clickedId]);
    let answers = props.answers.map((a, i) => <Answer key={i} answer={a} id={i} getClickedId={handleClick}/>);
    return (
        <div>
            {props.correctAnswer === undefined && <Preloader />}
            {props.correctAnswer !== undefined && answers}
        </div>
    )
}

const Answer = (props) => {
    let id = props.id;
    return (
        <div onClick={() => props.getClickedId(id)}>
            <button>{props.answer}</button>
        </div>
    )
}

const mapStateToProps = (state) => ({
    correctAnswer: state.game.currentQuestion.correct,
    answers: state.game.currentQuestion.content,
    level: state.game.currentLevel,
    questions: state.data.questions,
})

export default compose(connect(mapStateToProps, { setLevel, setCurrentQuestion, removeQuestion }))(Answers);
