import React, { useLayoutEffect } from 'react';
import { connect } from 'react-redux';
import { Link, Route } from 'react-router-dom';
import { compose } from 'redux';
import HandBlock from '../assets/Hand';
import mainText from '../assets/mainText';
import { getDataState } from '../redux/data-reducer';
import { setLevel } from '../redux/gameState-reducer';

const GameEnd = (props) => {
    useLayoutEffect(() => {
        props.getDataState();
    }, []);
    const finalScore = (level) => {
        return props.scoreTemplate !== undefined ? props.scoreTemplate[level - 1] : null;
    };
    return (
        <div>
            <HandBlock />
            <div>Total score: {finalScore(props.level)}</div>
            <MainText />
            <Link to='/game'>
                <button onClick={() => props.setLevel(1)}>Try Again</button>
            </Link>
            <Route path='gameEnd'></Route>
        </div>
    )
}

const mapStateToProps = (state) => ({
    level: state.game.currentLevel,
    scoreTemplate: state.config.score
})

export default compose(connect(mapStateToProps, { getDataState, setLevel }))(GameEnd);
