import React, { useEffect, useLayoutEffect, useState } from 'react';
import { connect } from 'react-redux';
import { Link, Route } from 'react-router-dom';
import { compose } from 'redux';
import { getDataState } from '../redux/data-reducer';
import { setLevel } from '../redux/gameState-reducer';

const GameEnd = (props) => {
    const [level, rememberLevel] = useState(null);
    useLayoutEffect(() => {
        props.getDataState();
    }, []);
    useEffect(() => {
        rememberLevel(props.level);
    }, [props.level])
    const finalScore = (level) => {
        if(props.scoreTemplate === undefined) {
            return null
        }
        return props.scoreTemplate[level - 1]
    };
    return (
        <div>
            {/* <GameLogo /> */}
            <div>Total score: {finalScore(props.level)}</div>
            {/* <MainText /> */}
            <Link to='/game'>
                <button>Try Again</button>
            </Link>
            <Route path='gameEnd'></Route>
        </div>
    )
}

const mapStateToProps = (state) => ({
    level: state.game.currentLevel,
    scoreTemplate: state.config.score
})

export default compose(connect(mapStateToProps, { getDataState, setLevel }))(GameEnd);
