import * as axios from "axios";

const instance = axios.create({
    baseURL: 'https://my-json-server.typicode.com/RaySkelen/demo/',
})

export const gameStateAPI = {
    getGameState() {
        return instance.get('gameState').then(response => {
            return console.log(response.data.gameInProgress);
        });
    },
    changeGameState(currentGameState) {
        return instance.put('gameState', {gameInProgress: currentGameState})
        .then(response =>{
            return (console.log(response.data))
        })
    },
    setCurrentQuestion(currentQuestion) {
        return instance.put('gameState', {currentQuestion}).then(response => {return console.log(response.data)})
    }
}
