import React, { useEffect } from 'react';
import { Link, Route } from 'react-router-dom';
import { compose } from 'redux';
import { connect } from 'react-redux';
import { setAppInitialized, setCurrentQuestion, setLevel, setScore } from '../redux/gameState-reducer';
import { Chance } from 'chance';

const Game = (props) => {
    useEffect((props) => {
        console.log(props);
        let questionNumber = Chance.natural({min: 0, max: props.questions.length});
        setCurrentQuestion();
    }, [props.questions])
    return (
        <div>
            {/* <Question question={props.currentQuestion.question} />
            <Answers answers={props.currentQuestion.content} correctAnswer={props.currentQuestion.correct} />
            <Score /> */}
        </div>
    )
}

const mapStateToProps = (state) => ({
    questions: state.data.questions,
    currentQuestion: state.game.currentQuestion,
    currentLevel: state.game.currentLevel,
    score: state.game.currentScore
})

export default compose(connect(mapStateToProps, { setCurrentQuestion, setScore, setLevel })(Game));
