import getAPI from "../api/api";

const SET_STATE = 'SET_STATE';

let initialState = getAPI.getData();

const dataReducer = (state = initialState, action) => {
    switch(action.type) {
        case SET_STATE: {
                return { ...action.state};
        }
        default: {
            return state
        }
    }
};

export const getState = () => (dispatch) => {
    getAPI.getConfig()
    .then(response => {
        dispatch(setState(response.data));
    })
};

export const setState = (state) => ({ type: SET_STATE, state });

export default dataReducer;
