import React from 'react';
import { compose } from 'redux';
import { connect } from 'react-redux';
import { setCurrentQuestion, setLevel } from '../redux/gameState-reducer';

const Answers = (props) => {
    let answers = props.answers.map((a, i) => <Answer key={i} answer={a} id={i}/>)
    return (
        <div>
            {answers}
        </div>
    )
}

const Answer = (props) => {
    const checkAnswer = (answer) => {
        if(props.correctAnswer === answer){
            setLevel(props.level + 1);
            let index = props.questions.indexOf(props.questions[Math.floor(Math.random() * props.questions.length)]);
            props.setCurrentQuestion(props.questions[index]);
            props.removeQuestion(index);
        }
    }
    return (
        <div>
            <button onClick={checkAnswer(props.id)}>{props.answer}</button>
        </div>
    )
}

const mapStateToProps = (state) => ({
    correctAnswer: state.game.currentQuestion.correct,
    answers: state.game.currentQuestion.content,
    level: state.game.currentLevel,
    questions: state.data.questions,
})


export default compose(connect(mapStateToProps, {setLevel, setCurrentQuestion}))(Answers);
